mapper.select:
SELECT
,yonghu.yonghu_uuid_number as yonghuUuidNumber ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.yonghu_xingge as yonghuXingge ,yonghu.yonghu_rushuishijian as yonghuRushuishijian ,yonghu.yonghu_qichuangshijian as yonghuQichuangshijian ,yonghu.yonghu_email as yonghuEmail ,yonghu.new_money as newMoney ,yonghu.xueyuan_types as xueyuanTypes ,yonghu.zhuanye_types as zhuanyeTypes ,yonghu.jinyong_types as jinyongTypes
FROM liuyan a left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and ( a.yonghu_id = #{params.yonghuId} )
and a.liuyan_uuid_number like CONCAT('%',#{params.liuyanUuidNumber},'%')
and a.liuyan_name like CONCAT('%',#{params.liuyanName},'%')
and a.liuyan_types = #{params.liuyanTypes}
and a.liuyan_content like CONCAT('%',#{params.liuyanContent},'%')
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.insert_time = #{params.insertTime}
and a.liuyan_huifu_content like CONCAT('%',#{params.liuyanHuifuContent},'%')
and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart})
and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd})
and a.update_time = #{params.updateTime}
and a.yonghu_id IS NOT NULL
and yonghu.yonghu_uuid_number like CONCAT('%',#{params.yonghuUuidNumber},'%')
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.yonghu_xingge like CONCAT('%',#{params.yonghuXingge},'%')
and yonghu.yonghu_rushuishijian like CONCAT('%',#{params.yonghuRushuishijian},'%')
and yonghu.yonghu_qichuangshijian like CONCAT('%',#{params.yonghuQichuangshijian},'%')
and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
and yonghu.new_money >= #{params.newMoneyStart}
and yonghu.new_money <= #{params.newMoneyEnd}
and a.new_money = #{params.newMoney}
and yonghu.xueyuan_types = #{params.xueyuanTypes}
and yonghu.zhuanye_types = #{params.zhuanyeTypes}
and yonghu.jinyong_types = #{params.jinyongTypes}
order by a.${params.sort} ${params.order}
