mapper.select:
SELECT



FROM yonghu  a



        and a.id in

        #{item}



        and a.username like CONCAT('%',#{params.username},'%')


        and a.password like CONCAT('%',#{params.password},'%')


        and a.yonghu_uuid_number like CONCAT('%',#{params.yonghuUuidNumber},'%')


        and a.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


        and a.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


        and a.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


        and a.yonghu_xingge like CONCAT('%',#{params.yonghuXingge},'%')


        and a.yonghu_rushuishijian like CONCAT('%',#{params.yonghuRushuishijian},'%')


        and a.yonghu_qichuangshijian like CONCAT('%',#{params.yonghuQichuangshijian},'%')


        and a.sex_types = #{params.sexTypes}


        and a.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


          and a.new_money >= #{params.newMoneyStart}   


          and a.new_money <= #{params.newMoneyEnd}   


        and a.new_money = #{params.newMoney}


        and a.xueyuan_types = #{params.xueyuanTypes}


        and a.zhuanye_types = #{params.zhuanyeTypes}


        and a.jinyong_types = #{params.jinyongTypes}



order by a.${params.sort} ${params.order}
